import '../components/Certificates.css'
import React, { useEffect, useState } from "react";
// import Project4 from '../Img/project2.1.png'
import c1 from '../Img/C1.png'
import c2 from '../Img/C2.png'
import c3 from '../Img/C3.png'
import c4 from '../Img/C4.png'
import c5 from '../Img/C5.png'
export const Certificates = () => {
    var arr= [c1, c2, c3,c4,c5];
    var [cerImg, setImg] = useState(c1);
    var [index, setIndix] = useState(0);
    setInterval(() => {
      if(index == 1)
      {
        $("#sliderimg").hide()
        $("#sliderimg1").show()
      }
      else{
        $("#sliderimg1").hide()
        $("#sliderimg").show()
      }
    }, 1000);

    return <>
        <div className="project1 component__space" id="Project1">
      <div className="heading"></div>

    <div id="imgSlider" >
        <img src={cerImg} alt='' id="sliderimg"/>
        <img src={c2} alt='' id="sliderimg1"/>
    </div>
</div>
    </>
}
